import styled from 'styled-components'; 



export const LeftbarContainer = styled.div`
  margin-top: 25px;
  margin-left: 20px;

  user-select: none;

  height: 100%;
  > div + div {
    padding-top: 16px;
  }
`

export const LibraryItem = styled.div`
  display: flex;
  justify-content: start;
  align-items: center;

  font-weight: 400;
  font-size: 14px;

  color: #c4c4c4;
  user-select: none;

  :hover {
    cursor: default;
    color: #fff;
  }

  :active {
    color: (0.2, '#a8a8a8')};
  }
`


export const Container = styled.div`
display:flex; 
flex-direction: column; 
margin-bottom: 15px;
height: 125px;
`

interface MenuItemProps{
  active?: boolean
}


export const MenuItem = styled.div<MenuItemProps>`
  display: flex;
  align-items: center;
  height: 42px;
  color: ${props => (props.active ? '#fff' : '#a8a8a8')};
  font-size: 20px;
  font-weight: bold;
  cursor: default;
  user-select: none;

  span {
    text-align: center;
    margin-left: 10px;
  }
  :hover {
    color: #edebeb;
  }
  :active {
    color: (0.2, '#a8a8a8')};
  }
  img{
    width:50px;
    padding-left: 20px; 

  }
`
